import React, { useState, useEffect } from 'react';
import { authService } from './services/auth';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import AgendamentosModal from './components/AgendamentosModal';
import StatusDashboard from './components/StatusDashboard';
import Navbar from './components/Navbar';
import Admin from './components/Admin';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [currentView, setCurrentView] = useState('dashboard');
  const [showAgendamentosModal, setShowAgendamentosModal] = useState(false);

  useEffect(() => {
    const initAuth = async () => {
      try {
        const currentUser = await authService.getCurrentUser();
        setUser(currentUser);
      } catch (error) {
        console.error('Erro ao verificar autenticação:', error);
      } finally {
        setLoading(false);
      }
    };

    initAuth();
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
  };

  const handleLogout = () => {
    authService.logout();
    setUser(null);
    setCurrentView('dashboard');
  };

  const handleViewChange = (view) => {
    if (view === 'pendentes') {
      setShowAgendamentosModal(true);
    } else {
      setCurrentView(view);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-700 flex items-center justify-center">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-400"></div>
          <p className="text-white mt-4">Carregando...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-700">
      <Navbar 
        user={user} 
        currentView={currentView}
        onViewChange={handleViewChange}
        onLogout={handleLogout}
      />
      
      {currentView === 'dashboard' && (
        <Dashboard 
          user={user} 
          onLogout={handleLogout}
          onNavigateToAdmin={() => setCurrentView('admin')}
          onNavigateToAgendamentos={() => setShowAgendamentosModal(true)}
        />
      )}
      
      {currentView === 'status' && (
        <StatusDashboard 
          user={user} 
          onLogout={handleLogout}
        />
      )}

      {currentView === 'admin' && (
        <Admin 
          user={user} 
          onLogout={handleLogout}
          onBack={() => setCurrentView('dashboard')}
        />
      )}

      {/* Agendamentos Modal */}
      {showAgendamentosModal && (
        <AgendamentosModal 
          user={user}
          onClose={() => setShowAgendamentosModal(false)}
        />
      )}
    </div>
  );
}

export default App;
