import React, { useState, useEffect } from 'react';
import { FaList, FaSearch, FaTrash, FaDownload, FaEdit, FaEye, FaCalendarAlt, FaStore, FaClock } from 'react-icons/fa';
import { apiService } from '../services/api';

const ListaAgendamentos = ({ user }) => {
  const [agendamentos, setAgendamentos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedItems, setSelectedItems] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showViewModal, setShowViewModal] = useState(false);
  const [selectedAgendamento, setSelectedAgendamento] = useState(null);

  useEffect(() => {
    fetchAgendamentos();
  }, []);

  const fetchAgendamentos = async () => {
    try {
      const data = await apiService.getAgendamentos();
      setAgendamentos(data);
    } catch (error) {
      console.error('Erro ao carregar agendamentos:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredAgendamentos = agendamentos.filter(agendamento => {
    const searchLower = searchTerm.toLowerCase();
    return (
      agendamento.nomeCliente?.toLowerCase().includes(searchLower) ||
      agendamento.loja?.toLowerCase().includes(searchLower) ||
      agendamento.telefone?.toLowerCase().includes(searchLower) ||
      agendamento.email?.toLowerCase().includes(searchLower) ||
      agendamento.status?.toLowerCase().includes(searchLower)
    );
  });

  const totalPages = Math.ceil(filteredAgendamentos.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentItems = filteredAgendamentos.slice(startIndex, endIndex);

  const handleSelectAll = () => {
    if (selectedItems.length === currentItems.length) {
      setSelectedItems([]);
    } else {
      setSelectedItems(currentItems.map(item => item.id));
    }
  };

  const handleSelectItem = (id) => {
    setSelectedItems(prev => 
      prev.includes(id) 
        ? prev.filter(item => item !== id)
        : [...prev, id]
    );
  };

  const handleDeleteSelected = async () => {
    if (selectedItems.length === 0) return;
    
    if (confirm(`Tem certeza que deseja excluir ${selectedItems.length} agendamento(s)?`)) {
      try {
        await Promise.all(
          selectedItems.map(id => apiService.deleteAgendamento(id))
        );
        setSelectedItems([]);
        fetchAgendamentos();
      } catch (error) {
        console.error('Erro ao excluir agendamentos:', error);
      }
    }
  };

  const handleExport = () => {
    const csvContent = [
      ['Nome do Cliente', 'Loja', 'Data', 'Horário', 'Status', 'Telefone', 'Email'],
      ...filteredAgendamentos.map(item => [
        item.nomeCliente || '',
        item.loja || '',
        item.data || '',
        item.hora || '',
        item.status || '',
        item.telefone || '',
        item.email || ''
      ])
    ].map(row => row.join(',')).join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `agendamentos_${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const handleEdit = (agendamento) => {
    setSelectedAgendamento(agendamento);
    setShowEditModal(true);
  };

  const handleView = (agendamento) => {
    setSelectedAgendamento(agendamento);
    setShowViewModal(true);
  };

  const handleUpdateAgendamento = async (updatedData) => {
    try {
      await apiService.updateAgendamento(selectedAgendamento.id, updatedData);
      setShowEditModal(false);
      fetchAgendamentos();
    } catch (error) {
      console.error('Erro ao atualizar agendamento:', error);
    }
  };

  const getStatusColor = (status) => {
    switch(status) {
      case 'pendente': return 'bg-yellow-100 text-yellow-800';
      case 'confirmado': return 'bg-green-100 text-green-800';
      case 'cancelado': return 'bg-red-100 text-red-800';
      case 'concluido': return 'bg-blue-100 text-blue-800';
      case 'agendar': return 'bg-purple-100 text-purple-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateStr) => {
    if (!dateStr) return 'Sem data';
    return new Date(dateStr).toLocaleDateString('pt-BR');
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-2 sm:px-4 py-4 sm:py-8">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
          <p className="text-gray-600 mt-4">Carregando agendamentos...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-2 sm:px-4 py-4 sm:py-8">
      <div className="bg-white rounded-xl sm:rounded-2xl shadow-xl p-4 sm:p-6 lg:p-8">
        {/* Filtros */}
        <div className="mb-4 sm:mb-6 p-3 sm:p-4 bg-gray-50 rounded-lg">
          <div className="max-w-md">
            <label className="block text-xs sm:text-sm font-medium text-gray-700 mb-2">Buscar:</label>
            <div className="relative">
              <input
                type="text"
                placeholder="Buscar por nome, endereço, telefone, email, loja..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full px-3 py-2 pl-10 text-sm border border-gray-300 rounded-lg focus:border-blue-500 focus:ring focus:ring-blue-200"
              />
              <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
            </div>
          </div>
        </div>

        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4 sm:mb-6 gap-4">
          <h2 className="text-xl sm:text-2xl lg:text-3xl font-bold text-gray-800">
            <FaList className="inline mr-2 sm:mr-3 text-blue-600" />
            <span className="block sm:inline">Agendamentos</span>
          </h2>
          
          {/* Ações em lote */}
          <div className="flex flex-wrap gap-2 w-full sm:w-auto">
            <button
              onClick={handleDeleteSelected}
              disabled={selectedItems.length === 0}
              className="px-3 sm:px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 text-xs sm:text-sm font-medium transition-all flex items-center justify-center disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <FaTrash className="mr-1 sm:mr-2" />
              <span className="hidden sm:inline">Excluir Selecionados</span>
              <span className="sm:hidden">Excluir</span>
            </button>
            <button
              onClick={handleExport}
              className="px-3 sm:px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 text-xs sm:text-sm font-medium transition-all flex items-center justify-center"
            >
              <FaDownload className="mr-1 sm:mr-2" />
              <span className="hidden sm:inline">Exportar</span>
            </button>
          </div>
        </div>

        {/* Controles de seleção */}
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4 gap-3">
          <div className="flex items-center gap-3">
            <input
              type="checkbox"
              checked={selectedItems.length === currentItems.length && currentItems.length > 0}
              onChange={handleSelectAll}
              className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
            />
            <label className="text-sm font-medium text-gray-700">
              Selecionar todos ({selectedItems.length})
            </label>
          </div>
          <div className="text-xs sm:text-sm text-gray-600">
            Mostrando {startIndex + 1} a {Math.min(endIndex, filteredAgendamentos.length)} de {filteredAgendamentos.length} agendamentos
          </div>
        </div>

        {/* Tabela responsiva */}
        <div className="overflow-x-auto -mx-2 sm:-mx-4">
          <div className="inline-block min-w-full align-middle">
            <div className="overflow-hidden shadow ring-1 ring-black ring-opacity-5 md:rounded-lg">
              <table className="min-w-full divide-y divide-gray-300">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-2 sm:px-4 py-2 sm:py-3 text-left text-xs sm:text-sm font-semibold text-gray-600 w-8">
                      <input
                        type="checkbox"
                        checked={selectedItems.length === currentItems.length && currentItems.length > 0}
                        onChange={handleSelectAll}
                        className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                      />
                    </th>
                    <th className="px-2 sm:px-4 py-2 sm:py-3 text-left text-xs sm:text-sm font-semibold text-gray-600">Cliente</th>
                    <th className="px-2 sm:px-4 py-2 sm:py-3 text-left text-xs sm:text-sm font-semibold text-gray-600 hidden sm:table-cell">Data/Hora</th>
                    <th className="px-2 sm:px-4 py-2 sm:py-3 text-left text-xs sm:text-sm font-semibold text-gray-600 hidden md:table-cell">Endereço</th>
                    <th className="px-2 sm:px-4 py-2 sm:py-3 text-left text-xs sm:text-sm font-semibold text-gray-600 hidden lg:table-cell">Loja</th>
                    <th className="px-2 sm:px-4 py-2 sm:py-3 text-left text-xs sm:text-sm font-semibold text-gray-600">Status</th>
                    <th className="px-2 sm:px-4 py-2 sm:py-3 text-center text-xs sm:text-sm font-semibold text-gray-600 min-w-[80px]">Ações</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {currentItems.map((agendamento) => (
                    <tr key={agendamento.id} className="hover:bg-gray-50">
                      <td className="px-2 sm:px-4 py-2 sm:py-3">
                        <input
                          type="checkbox"
                          checked={selectedItems.includes(agendamento.id)}
                          onChange={() => handleSelectItem(agendamento.id)}
                          className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                        />
                      </td>
                      <td className="px-2 sm:px-4 py-2 sm:py-3">
                        <div className="text-sm font-medium text-gray-900">{agendamento.nomeCliente}</div>
                        <div className="text-xs text-gray-500">{agendamento.telefone}</div>
                      </td>
                      <td className="px-2 sm:px-4 py-2 sm:py-3 hidden sm:table-cell">
                        <div className="text-sm text-gray-900">
                          <FaCalendarAlt className="inline mr-1 text-gray-400" />
                          {formatDate(agendamento.data)}
                        </div>
                        {agendamento.hora && (
                          <div className="text-xs text-gray-500">
                            <FaClock className="inline mr-1 text-gray-400" />
                            {agendamento.hora}
                          </div>
                        )}
                      </td>
                      <td className="px-2 sm:px-4 py-2 sm:py-3 hidden md:table-cell">
                        <div className="text-sm text-gray-900">
                          {agendamento.endereco?.logradouro}, {agendamento.endereco?.numero}
                        </div>
                        <div className="text-xs text-gray-500">
                          {agendamento.endereco?.bairro} - {agendamento.endereco?.cidade}
                        </div>
                      </td>
                      <td className="px-2 sm:px-4 py-2 sm:py-3 hidden lg:table-cell">
                        <div className="text-sm text-gray-900">
                          <FaStore className="inline mr-1 text-gray-400" />
                          {agendamento.loja}
                        </div>
                      </td>
                      <td className="px-2 sm:px-4 py-2 sm:py-3">
                        <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusColor(agendamento.status)}`}>
                          {agendamento.status}
                        </span>
                      </td>
                      <td className="px-2 sm:px-4 py-2 sm:py-3 text-center">
                        <div className="flex justify-center space-x-1">
                          <button
                            onClick={() => handleView(agendamento)}
                            className="p-1 text-blue-600 hover:text-blue-800 transition-colors"
                            title="Visualizar"
                          >
                            <FaEye className="text-sm" />
                          </button>
                          <button
                            onClick={() => handleEdit(agendamento)}
                            className="p-1 text-green-600 hover:text-green-800 transition-colors"
                            title="Editar"
                          >
                            <FaEdit className="text-sm" />
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        {/* Paginação */}
        <div className="flex flex-col sm:flex-row justify-between items-center mt-6 gap-4">
          <div className="text-xs sm:text-sm text-gray-600">
            Página {currentPage} de {totalPages}
          </div>
          <div className="flex gap-2">
            <button
              onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
              disabled={currentPage === 1}
              className="px-3 py-2 text-xs sm:text-sm bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <FaCalendarAlt className="inline mr-1" />
              Anterior
            </button>
            <button
              onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
              disabled={currentPage === totalPages}
              className="px-3 py-2 text-xs sm:text-sm bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Próxima
              <FaCalendarAlt className="inline ml-1" />
            </button>
          </div>
        </div>

        {/* Mensagem quando não há agendamentos */}
        {filteredAgendamentos.length === 0 && (
          <div className="text-center py-6 sm:py-8">
            <FaCalendarAlt className="text-2xl sm:text-4xl text-gray-400 mb-3 sm:mb-4 mx-auto" />
            <p className="text-sm sm:text-base text-gray-500">Nenhum agendamento encontrado</p>
          </div>
        )}
      </div>

      {/* Modal de Visualização */}
      {showViewModal && selectedAgendamento && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center p-2 sm:p-4 z-50">
          <div className="bg-white rounded-2xl shadow-2xl w-full max-w-4xl mx-2 sm:mx-4 max-h-[95vh] sm:max-h-[90vh] overflow-hidden">
            <div className="bg-blue-600 text-white p-6">
              <div className="flex justify-between items-center">
                <h3 className="text-xl font-bold flex items-center">
                  <FaEye className="mr-3" />
                  Detalhes do Agendamento
                </h3>
                <button
                  onClick={() => setShowViewModal(false)}
                  className="text-white hover:text-gray-200 transition-colors p-2 hover:bg-white hover:bg-opacity-20 rounded-lg"
                >
                  ✕
                </button>
              </div>
            </div>
            
            <div className="p-6 overflow-y-auto max-h-[calc(90vh-80px)]">
              <div className="space-y-6">
                {/* Informações do Cliente */}
                <div className="bg-blue-50 p-6 rounded-xl border border-blue-100">
                  <h4 className="text-lg font-semibold text-gray-800 mb-4">Informações do Cliente</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="text-sm font-medium text-gray-600">Nome</label>
                      <p className="text-gray-900 font-medium">{selectedAgendamento.nomeCliente}</p>
                    </div>
                    <div>
                      <label className="text-sm font-medium text-gray-600">Telefone</label>
                      <p className="text-gray-900 font-medium">{selectedAgendamento.telefone}</p>
                    </div>
                    <div>
                      <label className="text-sm font-medium text-gray-600">Email</label>
                      <p className="text-gray-900 font-medium">{selectedAgendamento.email}</p>
                    </div>
                    <div>
                      <label className="text-sm font-medium text-gray-600">Status</label>
                      <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusColor(selectedAgendamento.status)}`}>
                        {selectedAgendamento.status}
                      </span>
                    </div>
                  </div>
                </div>

                {/* Informações do Agendamento */}
                <div className="bg-green-50 p-6 rounded-xl border border-green-100">
                  <h4 className="text-lg font-semibold text-gray-800 mb-4">Detalhes do Agendamento</h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="text-sm font-medium text-gray-600">Loja</label>
                      <p className="text-gray-900 font-medium">{selectedAgendamento.loja}</p>
                    </div>
                    <div>
                      <label className="text-sm font-medium text-gray-600">Data</label>
                      <p className="text-gray-900 font-medium">{formatDate(selectedAgendamento.data)}</p>
                    </div>
                    <div>
                      <label className="text-sm font-medium text-gray-600">Horário</label>
                      <p className="text-gray-900 font-medium">{selectedAgendamento.hora || 'Não definido'}</p>
                    </div>
                    <div>
                      <label className="text-sm font-medium text-gray-600">Observações</label>
                      <p className="text-gray-900 font-medium">{selectedAgendamento.observacoes || 'Nenhuma'}</p>
                    </div>
                  </div>
                </div>

                {/* Endereço */}
                {selectedAgendamento.endereco && (
                  <div className="bg-purple-50 p-6 rounded-xl border border-purple-100">
                    <h4 className="text-lg font-semibold text-gray-800 mb-4">Endereço</h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                      <div>
                        <label className="text-sm font-medium text-gray-600">CEP</label>
                        <p className="text-gray-900 font-medium">{selectedAgendamento.endereco.cep}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Logradouro</label>
                        <p className="text-gray-900 font-medium">{selectedAgendamento.endereco.logradouro}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Número</label>
                        <p className="text-gray-900 font-medium">{selectedAgendamento.endereco.numero}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Complemento</label>
                        <p className="text-gray-900 font-medium">{selectedAgendamento.endereco.complemento || 'N/A'}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Bairro</label>
                        <p className="text-gray-900 font-medium">{selectedAgendamento.endereco.bairro}</p>
                      </div>
                      <div>
                        <label className="text-sm font-medium text-gray-600">Cidade</label>
                        <p className="text-gray-900 font-medium">{selectedAgendamento.endereco.cidade}</p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
            
            <div className="bg-gray-50 p-6 border-t">
              <button
                onClick={() => setShowViewModal(false)}
                className="w-full bg-blue-600 text-white px-6 py-3 rounded-lg font-medium hover:bg-blue-700 transition-all"
              >
                Fechar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListaAgendamentos;
