import React from 'react';
import { FaCalendarAlt, FaChartBar, FaHome, FaSignOutAlt, FaList, FaPlus, FaTable, FaUsers } from 'react-icons/fa';

const Navbar = ({ user, currentView, onViewChange, onLogout }) => {
  const navItems = [
    { id: 'dashboard', label: 'Dashboard', icon: FaHome },
    { id: 'formulario', label: 'Novo Agendamento', icon: FaPlus },
    { id: 'pendentes', label: 'Agendamentos Pendentes', icon: FaCalendarAlt },
    { id: 'lista', label: 'Lista Completa', icon: FaTable },
    { id: 'status', label: 'Status Dashboard', icon: FaChartBar },
    ...(user.role === 'admin' ? [{ id: 'admin', label: 'Gerenciar Usuários', icon: FaUsers }] : [])
  ];

  return (
    <nav className="bg-white/10 backdrop-blur-lg border-b border-white/20">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <div className="flex items-center space-x-3">
            <div className="flex items-center justify-center w-10 h-10 bg-blue-600 rounded-lg">
              <FaCalendarAlt className="text-white text-xl" />
            </div>
            <h1 className="text-xl font-bold text-white">Agenda Medição</h1>
          </div>

          {/* Navigation Items */}
          <div className="hidden md:flex items-center space-x-1">
            {navItems.map(item => {
              const Icon = item.icon;
              return (
                <button
                  key={item.id}
                  onClick={() => onViewChange(item.id)}
                  className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors duration-200 ${
                    currentView === item.id
                      ? 'bg-blue-600 text-white'
                      : 'text-slate-300 hover:bg-white/10 hover:text-white'
                  }`}
                >
                  <Icon className="text-lg" />
                  <span>{item.label}</span>
                </button>
              );
            })}
          </div>

          {/* User Info & Logout */}
          <div className="flex items-center space-x-4">
            <div className="text-right hidden sm:block">
              <p className="text-white text-sm font-medium">{user.email}</p>
              <p className="text-slate-400 text-xs">
                {user.role === 'admin' ? 'Administrador' : 'Usuário'}
              </p>
            </div>
            <button
              onClick={onLogout}
              className="flex items-center space-x-2 bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg transition-colors duration-200"
            >
              <FaSignOutAlt />
              <span className="hidden sm:inline">Sair</span>
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        <div className="md:hidden py-3 border-t border-white/10">
          <div className="flex flex-col space-y-1">
            {navItems.map(item => {
              const Icon = item.icon;
              return (
                <button
                  key={item.id}
                  onClick={() => onViewChange(item.id)}
                  className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors duration-200 ${
                    currentView === item.id
                      ? 'bg-blue-600 text-white'
                      : 'text-slate-300 hover:bg-white/10 hover:text-white'
                  }`}
                >
                  <Icon className="text-lg" />
                  <span>{item.label}</span>
                </button>
              );
            })}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
