import React, { useState, useEffect } from 'react';
import { FaCalendarAlt, FaTrophy, FaCheckCircle, FaRoute, FaCalendarWeek, FaSyncAlt, FaClock, FaCheckCircle as FaCheck } from 'react-icons/fa';
import { apiService } from '../services/api';

const DashboardCompleto = ({ user }) => {
  const [agendamentos, setAgendamentos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [roteiroHoje, setRoteiroHoje] = useState([]);
  const [roteirosProximosDias, setRoteirosProximosDias] = useState([]);
  const [periodFilter, setPeriodFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const data = await apiService.getAgendamentos();
      setAgendamentos(data);
      
      // Processar dados para dashboard
      processarDashboardData(data);
    } catch (error) {
      console.error('Erro ao carregar dados do dashboard:', error);
    } finally {
      setLoading(false);
    }
  };

  const processarDashboardData = (data) => {
    // Separar agendamentos por status
    const agendados = data.filter(a => a.status === 'pendente' || a.status === 'agendar');
    const concluidos = data.filter(a => a.status === 'concluido');
    
    // Gerar roteiro de hoje
    const hoje = new Date().toISOString().split('T')[0];
    const agendamentosHoje = data.filter(a => a.data === hoje);
    const roteiroHojeOrdenado = ordenarRoteiro(agendamentosHoje);
    setRoteiroHoje(roteiroHojeOrdenado);
    
    // Gerar roteiros dos próximos dias
    const proximosDias = gerarRoteirosProximosDias(data);
    setRoteirosProximosDias(proximosDias);
  };

  const ordenarRoteiro = (agendamentos) => {
    // Simulação de ordenação por proximidade geográfica
    // Na implementação real, usaríamos APIs de geolocalização
    return agendamentos.sort((a, b) => {
      if (a.hora && b.hora) {
        return a.hora.localeCompare(b.hora);
      }
      return 0;
    });
  };

  const gerarRoteirosProximosDias = (data) => {
    const dias = [];
    const hoje = new Date();
    
    for (let i = 1; i <= 7; i++) {
      const dataFutura = new Date(hoje);
      dataFutura.setDate(hoje.getDate() + i);
      const dataStr = dataFutura.toISOString().split('T')[0];
      
      const agendamentosDia = data.filter(a => a.data === dataStr);
      if (agendamentosDia.length > 0) {
        dias.push({
          data: dataStr,
          dataFormatada: dataFutura.toLocaleDateString('pt-BR', { 
            weekday: 'long', 
            day: '2-digit', 
            month: '2-digit' 
          }),
          agendamentos: ordenarRoteiro(agendamentosDia)
        });
      }
    }
    
    return dias;
  };

  const getFilteredAgendamentos = () => {
    let filtered = [...agendamentos];
    
    // Filtrar por período
    if (periodFilter !== 'all') {
      const hoje = new Date();
      hoje.setHours(0, 0, 0, 0);
      
      if (periodFilter === 'today') {
        const hojeStr = hoje.toISOString().split('T')[0];
        filtered = filtered.filter(a => a.data === hojeStr);
      } else if (periodFilter === 'week') {
        const fimDaSemana = new Date(hoje);
        fimDaSemana.setDate(hoje.getDate() + 7);
        filtered = filtered.filter(a => {
          const dataAg = new Date(a.data);
          return dataAg >= hoje && dataAg <= fimDaSemana;
        });
      } else if (periodFilter === 'month') {
        const fimDoMes = new Date(hoje.getFullYear(), hoje.getMonth() + 1, 0);
        filtered = filtered.filter(a => {
          const dataAg = new Date(a.data);
          return dataAg >= hoje && dataAg <= fimDoMes;
        });
      }
    }
    
    // Filtrar por status
    if (statusFilter !== 'all') {
      filtered = filtered.filter(a => a.status === statusFilter);
    }
    
    return filtered;
  };

  const atualizarRoteiroHoje = () => {
    fetchDashboardData();
  };

  const atualizarRoteirosProximosDias = () => {
    fetchDashboardData();
  };

  const filteredAgendamentos = getFilteredAgendamentos();
  const agendados = filteredAgendamentos.filter(a => a.status === 'pendente' || a.status === 'agendar');
  const concluidos = filteredAgendamentos.filter(a => a.status === 'concluido');

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-2 sm:px-4 py-4 sm:py-8">
        <div className="text-center">
          <div className="inline-block animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
          <p className="text-gray-600 mt-4">Carregando dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-2 sm:px-4 py-4 sm:py-8">
      {/* Cards de Estatísticas */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-3 sm:gap-4 lg:gap-6 mb-6 sm:mb-8">
        <div className="bg-white p-4 sm:p-6 rounded-lg shadow-lg">
          <div className="flex items-center justify-between">
            <div className="min-w-0 flex-1">
              <h3 className="text-lg font-semibold text-gray-800">Total Agendamentos</h3>
              <p className="text-3xl font-bold text-blue-600">{filteredAgendamentos.length}</p>
            </div>
            <div className="p-2 sm:p-3 bg-blue-100 rounded-full flex-shrink-0 ml-2">
              <FaCalendarAlt className="text-blue-600 text-sm sm:text-lg lg:text-xl" />
            </div>
          </div>
        </div>
        
        <div className="bg-white p-4 sm:p-6 rounded-lg shadow-lg">
          <div className="flex items-center justify-between">
            <div className="min-w-0 flex-1">
              <h3 className="text-lg font-semibold text-gray-800">Agendados</h3>
              <p className="text-3xl font-bold text-yellow-600">{agendados.length}</p>
            </div>
            <div className="p-2 sm:p-3 bg-yellow-100 rounded-full flex-shrink-0 ml-2">
              <FaTrophy className="text-yellow-600 text-sm sm:text-lg lg:text-xl" />
            </div>
          </div>
        </div>
        
        <div className="bg-white p-4 sm:p-6 rounded-lg shadow-lg">
          <div className="flex items-center justify-between">
            <div className="min-w-0 flex-1">
              <h3 className="text-lg font-semibold text-gray-800">Concluídos</h3>
              <p className="text-3xl font-bold text-green-600">{concluidos.length}</p>
            </div>
            <div className="p-2 sm:p-3 bg-green-100 rounded-full flex-shrink-0 ml-2">
              <FaCheckCircle className="text-green-600 text-sm sm:text-lg lg:text-xl" />
            </div>
          </div>
        </div>
      </div>

      {/* Detalhes por Status */}
      <div className="bg-white rounded-lg shadow-lg p-4 mb-6">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4">
          <h3 className="text-lg font-bold text-gray-800 mb-2 sm:mb-0">Detalhes dos Agendamentos</h3>
          <div className="flex gap-2 flex-wrap">
            <select 
              value={periodFilter}
              onChange={(e) => setPeriodFilter(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:border-blue-500 focus:ring focus:ring-blue-200"
            >
              <option value="all">Todos os períodos</option>
              <option value="today">Hoje</option>
              <option value="week">Esta semana</option>
              <option value="month">Este mês</option>
            </select>
            <select 
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:border-blue-500 focus:ring focus:ring-blue-200"
            >
              <option value="all">Todos os status</option>
              <option value="pendente">Pendentes</option>
              <option value="agendar">Agendar</option>
              <option value="concluido">Concluídos</option>
            </select>
          </div>
        </div>
        
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Agendados */}
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center justify-between mb-4">
              <h4 className="text-md font-semibold text-gray-800 flex items-center">
                <FaClock className="text-yellow-600 mr-2" />
                Agendados
              </h4>
              <span className="bg-yellow-100 text-yellow-800 px-2 py-1 rounded-full text-sm font-medium">
                {agendados.length}
              </span>
            </div>
            <div className="space-y-3 max-h-96 overflow-y-auto">
              {agendados.length === 0 ? (
                <div className="text-center text-gray-500 py-4">
                  <p>Nenhum agendamento encontrado</p>
                </div>
              ) : (
                agendados.map(agendamento => (
                  <div key={agendamento.id} className="bg-white p-3 rounded-lg border border-gray-200">
                    <div className="flex justify-between items-start">
                      <div>
                        <p className="font-medium text-gray-900">{agendamento.nomeCliente}</p>
                        <p className="text-sm text-gray-600">{agendamento.loja}</p>
                        {agendamento.data && (
                          <p className="text-xs text-gray-500">
                            {new Date(agendamento.data).toLocaleDateString('pt-BR')}
                            {agendamento.hora && ` às ${agendamento.hora}`}
                          </p>
                        )}
                      </div>
                      <span className="px-2 py-1 bg-yellow-100 text-yellow-800 text-xs rounded-full">
                        {agendamento.status}
                      </span>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Concluídos */}
          <div className="bg-gray-50 rounded-lg p-4">
            <div className="flex items-center justify-between mb-4">
              <h4 className="text-md font-semibold text-gray-800 flex items-center">
                <FaCheck className="text-green-600 mr-2" />
                Concluídos
              </h4>
              <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-sm font-medium">
                {concluidos.length}
              </span>
            </div>
            <div className="space-y-3 max-h-96 overflow-y-auto">
              {concluidos.length === 0 ? (
                <div className="text-center text-gray-500 py-4">
                  <p>Nenhum agendamento concluído</p>
                </div>
              ) : (
                concluidos.map(agendamento => (
                  <div key={agendamento.id} className="bg-white p-3 rounded-lg border border-gray-200">
                    <div className="flex justify-between items-start">
                      <div>
                        <p className="font-medium text-gray-900">{agendamento.nomeCliente}</p>
                        <p className="text-sm text-gray-600">{agendamento.loja}</p>
                        {agendamento.data && (
                          <p className="text-xs text-gray-500">
                            {new Date(agendamento.data).toLocaleDateString('pt-BR')}
                          </p>
                        )}
                      </div>
                      <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                        {agendamento.status}
                      </span>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Roteiro Otimizado do Dia */}
      <div className="bg-white rounded-lg shadow-lg p-4 mb-6">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4">
          <h3 className="text-lg font-bold text-gray-800 mb-2 sm:mb-0 flex items-center">
            <FaRoute className="text-blue-600 mr-2" />
            Roteiro Otimizado de Hoje
            <span className="text-sm font-normal text-gray-600 ml-2">
              (1º agendamento fixo → próximos otimizados)
            </span>
          </h3>
          <div className="flex gap-2">
            <button 
              onClick={atualizarRoteiroHoje}
              className="px-3 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 text-sm font-medium transition-all flex items-center"
            >
              <FaSyncAlt className="mr-2" />
              Atualizar Roteiro
            </button>
          </div>
        </div>
        
        <div className="space-y-3">
          {roteiroHoje.length === 0 ? (
            <div className="text-center text-gray-500 py-8">
              <FaRoute className="text-3xl mb-2 mx-auto" />
              <p className="text-sm">Nenhum agendamento para hoje</p>
            </div>
          ) : (
            roteiroHoje.map((agendamento, index) => (
              <div key={agendamento.id} className="bg-gray-50 p-4 rounded-lg border border-gray-200">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-blue-600 text-white rounded-full flex items-center justify-center font-bold text-sm">
                      {index + 1}
                    </div>
                    <div>
                      <p className="font-medium text-gray-900">{agendamento.nomeCliente}</p>
                      <p className="text-sm text-gray-600">{agendamento.loja}</p>
                      {agendamento.hora && (
                        <p className="text-xs text-gray-500">Horário: {agendamento.hora}</p>
                      )}
                    </div>
                  </div>
                  <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                    {agendamento.status}
                  </span>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Roteiros dos Próximos Dias */}
      <div className="bg-white rounded-lg shadow-lg p-4 mb-6">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-4">
          <h3 className="text-lg font-bold text-gray-800 mb-2 sm:mb-0 flex items-center">
            <FaCalendarWeek className="text-purple-600 mr-2" />
            Roteiros dos Próximos Dias
            <span className="text-sm font-normal text-gray-600 ml-2">
              (planejamento semanal)
            </span>
          </h3>
          <div className="flex gap-2">
            <button 
              onClick={atualizarRoteirosProximosDias}
              className="px-3 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 text-sm font-medium transition-all flex items-center"
            >
              <FaSyncAlt className="mr-2" />
              Atualizar Tudo
            </button>
          </div>
        </div>
        
        <div className="space-y-4">
          {roteirosProximosDias.length === 0 ? (
            <div className="text-center text-gray-500 py-8">
              <FaCalendarWeek className="text-3xl mb-2 mx-auto" />
              <p className="text-sm">Nenhum agendamento para os próximos dias</p>
            </div>
          ) : (
            roteirosProximosDias.map(dia => (
              <div key={dia.data} className="bg-gray-50 p-4 rounded-lg border border-gray-200">
                <h4 className="font-semibold text-gray-800 mb-3">{dia.dataFormatada}</h4>
                <div className="space-y-2">
                  {dia.agendamentos.map((agendamento, index) => (
                    <div key={agendamento.id} className="bg-white p-3 rounded border border-gray-200">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-3">
                          <div className="w-6 h-6 bg-purple-600 text-white rounded-full flex items-center justify-center font-bold text-xs">
                            {index + 1}
                          </div>
                          <div>
                            <p className="font-medium text-gray-900 text-sm">{agendamento.nomeCliente}</p>
                            <p className="text-xs text-gray-600">{agendamento.loja}</p>
                            {agendamento.hora && (
                              <p className="text-xs text-gray-500">{agendamento.hora}</p>
                            )}
                          </div>
                        </div>
                        <span className="px-2 py-1 bg-purple-100 text-purple-800 text-xs rounded-full">
                          {agendamento.status}
                        </span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default DashboardCompleto;
